if (msg.topic !== "moveX") return msg

const distance = msg.payload.distance
const direction = msg.payload.direction
const pulsesPerMeter = global.get("pulsesPerMeter")

return {
  payload: {
    count: distance * pulsesPerMeter,
    direction
  }
}

const maxPositionXInPulses = global.get("maxPositionXInPulses")
const maxPositionXInMeters = global.get("maxPositionXInMeters")

const pulsesPerMeter = maxPositionXInPulses / maxPositionXInMeters

global.set("pulsesPerMeter", pulsesPerMeter)
